import React, {Component} from 'react';



class Option extends Component {


    render(){
        const {type, options, selected, onChange} = this.props;
        var res = options.map((choice,index) =>
            <div>
                <input 
                    type="radio"
                    name={type}
                    value={choice}
                    key={index}
                    checked={selected=== choice}
                    onChange= {onChange}
                />
            </div>
        ) 
        return (
          <div>
           {res}
           </div>
        )
      }



}

export default Option
