import React, {Component} from 'react';

const hiddenUrl = "https://www.rahit-li.co.il/wp-content/uploads/2017/02/present-147168_960_720.png"


class Image extends Component {


        
    render(){
        const {id, image, handleClick, found, selected} = this.props;
            

        // var wasFound = this.wasFound();
        // var needToBeOpen = this.needToBeOpen();
        var classNames = selected? "image animated flip delay-0s " : "image "
        var style = found? {opacity: 0.5} : {opacity: 1};
        // var src = wasFound? this.state.image 
        //         : needToBeOpen?  this.state.image: hiddenUrl 
        return (
            <div className="col-3 col-sm-3 col-md-3 col-lg-2">
                    <div className="img-thumbnail container2">
                    <img 
                        id={id} 
                        className={classNames}
                        src={(found || selected)? image : hiddenUrl} 
                        alt="Loading" 
                        onClick={(event) => handleClick(event.target.id)}
                        style={style}
                    />
                </div>
            </div>

            )
    }
}


