import React, {Component} from 'react';
import Option from './Option'


class Settings extends Component {

    constructor(){
        super()
        this.state = {
          type: "cat",
          level: "hard",
        }
      }


    handleLevelChange = (event) =>{
        this.setState({Level: event.target.value})
    }

    handleTypeChange = (event) => {
        this.setState({Type: event.target.value})
    }

    render(){
        return (
          <div>
              <Option optionType="Animal" optionsData={["Cats","Dogs"]} selected={this.state.type} onChange={this.handleTypeChange}/>
              <Option optionType="Level" optionsData={["Easy","Medium","Hard"]} selected={this.state.level} onChange={this.handleLevelChange}/>

          </div>
        )
      }

}




export default Settings
