import React, {Component} from 'react';
import MemoryGame from './Components/MemoryGame'
import Settings from './Components/Settings'
import 'bootstrap/dist/css/bootstrap.min.css';
import Sidebar from "react-sidebar";
import { Navbar,Nav,NavDropdown,Form,FormControl,Button } from 'react-bootstrap';


class App extends Component {

  constructor(props) {

    super(props);

    this.state = {
        sidebarOpen: false,
        type: "cats",
        level: "hard",
        game: () => <MemoryGame type={this.state.type} level={this.state.level}/>
    };
    
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }

  handleLevelChange = (event) =>{
    this.setState({level: event.target.value})
  }

  handleTypeChange = (event) => {
    this.setState({type: event.target.value})
  }

  handleResetGame = (event) => {
    console.log("hey i wash heree")
    this.setState({game: ()=> <MemoryGame type={this.state.type} level={this.state.level}/>})
  }

  render(){

    const Game = this.state.game;
    return (
      <div>
      <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#link">Link</Nav.Link>

        </Nav>
        <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-success">Search</Button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
      <div  className="container">
        <Sidebar
          sidebar={
            <div>
              <h3 className="text-center"><b> Settings</b></h3>
                <br/><br/>
              <Settings 
                handleLevelChange={this.handleLevelChange} 
                handleTypeChange={this.handleTypeChange} 
                handleResetGame={this.handleResetGame} 
                type={this.state.type} 
                level={this.state.level} 
              />
            </div>
          }
          open={this.state.sidebarOpen}
          onSetOpen={this.onSetSidebarOpen}
          styles={{ sidebar: { background: "DeepSkyBlue" }}}
        >
        <img className="img-fluid"  src="/hamburger.png"  alt="loading" style={{width: "5%", }}onClick={() => this.onSetSidebarOpen(true)} />
      </Sidebar>
        <Game/>

      </div>
      </div>
    )
  }

}

export default App;
