import React, {Component} from 'react';
import MemoryGame from './Components/MemoryGame'
import Settings from './Components/Settings'

import Sidebar from "react-sidebar";



class App extends Component {

  constructor(props) {

    super(props);

    this.state = {
        sidebarOpen: false,
        type: "cats",
        level: "hard",
    };
    
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }

  handleLevelChange = (event) =>{
    this.setState({level: event.target.value})
  }

  handleTypeChange = (event) => {
    this.setState({type: event.target.value})
  }


  render(){

    return (
      <div>
        <Sidebar
          sidebar={
            <div className="container">
              <h3 className="text-center"><b> Settings</b></h3>
                <br/><br/>
              <Settings handleLevelChange={this.handleLevelChange} handleTypeChange={this.handleTypeChange} type={this.state.type} level={this.state.level} />
            </div>
          }
          open={this.state.sidebarOpen}
          onSetOpen={this.onSetSidebarOpen}
          styles={{ sidebar: { background: "DeepSkyBlue" }}}
        >
        <input type="image" src="/hamburger.png" alt="loading" onClick={() => this.onSetSidebarOpen(true)} />
      </Sidebar>
        <MemoryGame type={this.state.type} level={this.state.level} />
      </div>
    )
  }

}

export default App;
