import React, {Component} from 'react';
import MemoryGame from './Components/MemoryGame'
import Sidebar from "react-sidebar";



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      sidebarOpen: false
    };
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }

  render(){
    return (
      <div>
        <Sidebar
          sidebar={
            <div className="container">
              <h3><b> Settings</b></h3>
              <br/><br/>
              <h4><b><u>Image Type:</u></b></h4>
              <input type="radio" name="imageType" value="Cats"/>  Cats <br/>
              <input type="radio" name="imageType" value="Dogs"/>  Dogs <br/>
              <br/><br/>
              <h4><b><u>Level:</u></b></h4>
              <input type="radio" name="Level" value="Easy"/>  Easy <br/>
              <input type="radio" name="Level" value="Medium"/>  Medium <br/>
              <input type="radio" name="Level" value="Hard"/>  Hard <br/>

              <br/><br/>
              <button>Reset Game</button>
            </div>
          }
          
          open={this.state.sidebarOpen}
          onSetOpen={this.onSetSidebarOpen}
          styles={{ sidebar: { background: "DeepSkyBlue " } }}
        >
        <input type="image" src="/hamburger.png" alt="loading" onClick={() => this.onSetSidebarOpen(true)} />
        
      </Sidebar>
        <MemoryGame/>
      </div>
    )
  }

}

export default App;
