import React, {Component} from 'react';
import {shuffle} from '../utils'
import Image from './Image'
import logo from '../Resources/logo.png';


class MemoryGame extends Component {

    constructor(){
        super()
        this.state = {
          images  : [],
          selectedImages: [],
          foundPairs: []
        }
      }

      handleClick = (imageID) => {
        const {images, selectedImages, foundPairs} = this.state;
        //do logic only if the image is not already guessed or selected 
        if (!(foundPairs.includes(images[imageID]) || selectedImages.includes(images[imageID]))){
          //if it's the first image of the guessed pair then just add it to the array
          if (selectedImages.length===0){
            this.setState({selectedImages: [images[imageID]]})
          //if it's is the second image of the guessed pair then check if both images are equal and reset selected images
          } else if (selectedImages.length===1){
              this.setState((prevState) => { 
                return {selectedImages: [...prevState.selectedImages,images[imageID]]}
              } , () => {
                        this.setState((prevState)=> {
                          const { selectedImages, foundPairs} = prevState;
                          if (selectedImages[0].url===selectedImages[1].url){
                              return {
                                foundPairs: [...foundPairs,selectedImages[0],selectedImages[1]],
                                selectedImages: []
                              }
                          } else setTimeout(() => {
                              this.setState({selectedImages: []})
                            }, 3000);
                      })})
            } 
      }
    }
    
      componentDidMount(){
        fetch("https://api.thecatapi.com/v1/images/search?limit=12")
          .then(result => result.json())
          .then(result =>{
              result = [...result,...result];
              shuffle(result);
              var key = 0;
              var urls = result.map(element => {
                console.log(element.url)
                return {key: key++, url: element.url}
              });
              this.setState({images: urls})
          })
      }
      

      render(){
        const {images, selectedImages, foundPairs} = this.state;
        var imagesComp = images.map(element => {
          return <Image 
                    key={element.key} 
                    id={element.key}
                    image={element.url}  
                    handleClick={this.handleClick}
                    found={foundPairs.includes(element)}
                    selected={selectedImages.includes(element)}
                  />
        });

        return (
          <div>
            <div> 
               <img src="..\\Resources\\Cats\\Image\\1.jpg" alt="loading"/>
             </div>
           <div className="container">
            <div class="text-center">
               <img src={logo} alt="loading" class="image responsive"/>
            </div>
                 <div className="container">
                  <div className="row mt-5">
                     {imagesComp}
                   </div>
                 </div>
           </div>
          </div>
        )
      }
    
}

export default MemoryGame;
